import {React} from 'react';
import { Avatar } from "@chakra-ui/avatar";
import { Tooltip } from "@chakra-ui/tooltip";
import ScrollableFeed from "react-scrollable-feed";
import {
  isLastMessage,
  isSameSender,
  isSameSenderMargin,
  isSameUser,
} from "../../config/ChatLogics";
import "./Pay.css";


const RequestToken=(props)=>{


    return(
        <div style={{ display: "flex" }} key={props.id}>
              {(isSameSender(props.messages, props.m, props.i, props.user_id) ||
                isLastMessage(props.messages, props.i, props.user_id)) && (
                <Tooltip
                  label={props.sender_name}
                  placement="bottom-start"
                  hasArrow
                >
                  <Avatar
                    mt="7px"
                    mr={1}
                    size="sm"
                    cursor="pointer"
                    name={props.sender_name}
                    src={props.sender_pic}
                  />
                </Tooltip>
              )}
              <div
                style={{
                  backgroundColor: `${
                    props.sender_id === props.user_id
                      ? "rgb(0,57,62)"
                      : "rgb(0,43,48)"
                  }`,
                  marginLeft: isSameSenderMargin(props.messages, props.m, props.i, props.user_id),
                  marginTop: isSameUser(props.messages, props.m, props.i, props.user_id) ? 3 : 10,
                  borderRadius: "20px",
                  padding: "5px 15px",
                  maxWidth: "75%",
                  minWidth:"200px",
                  color: "white",
                  height:"130px",
                }}
              >
                <p class="payment_user">Payment to karupaiya</p>
                <p class="payment_amount">${props.content}</p>
                <div style={{"display":"flex"}}>
                  <p class="payment_status">paid&ensp;&ensp;</p>
                  <p class="payment_date">dec 30th</p>
                </div>
              </div>
            </div>
    );
}


export default RequestToken;
